"use client";

import { animate } from "framer-motion";
import { useEffect, useState, useRef } from "react";

export function useAnimatedText(text: string, delimiter: string = "") {
  const [displayedIndex, setDisplayedIndex] = useState(0);
  const prevTextRef = useRef("");
  const displayedIndexRef = useRef(0);
  const animationControlsRef = useRef<ReturnType<typeof animate> | null>(null);

  // Keep displayedIndexRef in sync
  useEffect(() => {
    displayedIndexRef.current = displayedIndex;
  }, [displayedIndex]);

  useEffect(() => {
    // Handle empty text
    if (!text) {
      if (prevTextRef.current.length > 0) {
        setDisplayedIndex(0);
        displayedIndexRef.current = 0;
        prevTextRef.current = "";
        if (animationControlsRef.current) {
          animationControlsRef.current.stop();
          animationControlsRef.current = null;
        }
      }
      return;
    }

    // If text hasn't changed, do nothing
    if (text === prevTextRef.current) {
      return;
    }

    const prevText = prevTextRef.current;
    const parts = text.split(delimiter).filter(p => p.length > 0);
    
    if (parts.length === 0) {
      prevTextRef.current = text;
      return;
    }

    // Check if this is incremental text (new text starts with previous)
    let startIndex = 0;
    if (text.startsWith(prevText) && prevText.length > 0) {
      // Incremental update - continue from current displayed position
      startIndex = Math.min(displayedIndexRef.current, parts.length);
    } else {
      // New text (not incremental) - start from beginning
      startIndex = 0;
      setDisplayedIndex(0);
      displayedIndexRef.current = 0;
    }

    const targetIndex = parts.length;
    const remainingParts = targetIndex - startIndex;

    if (remainingParts <= 0) {
      // Already at target, just update ref
      if (displayedIndex !== targetIndex) {
        setDisplayedIndex(targetIndex);
        displayedIndexRef.current = targetIndex;
      }
      prevTextRef.current = text;
      return;
    }

    // Stop existing animation
    if (animationControlsRef.current) {
      animationControlsRef.current.stop();
    }

    // Calculate duration - animate at a readable pace
    const durationPerPart = delimiter === "" ? 0.02 : // Character: 20ms
                            delimiter === " " ? 0.12 : // Word: 120ms per word
                            0.3; // Chunk: 300ms
    
    // Cap the duration so it doesn't take too long, but ensure minimum for smoothness
    const calculatedDuration = remainingParts * durationPerPart;
    const duration = Math.max(Math.min(calculatedDuration, 3), 0.15); // Max 3s, min 150ms
    
    console.log(`[AnimatedText] Animating ${remainingParts} parts (${startIndex} -> ${targetIndex}), duration: ${duration.toFixed(2)}s`);

    const controls = animate(startIndex, targetIndex, {
      duration: duration,
      ease: "linear", // Linear for consistent word-by-word display
      onUpdate(latest) {
        const currentIndex = Math.floor(latest);
        setDisplayedIndex(currentIndex);
        displayedIndexRef.current = currentIndex;
      },
      onComplete: () => {
        setDisplayedIndex(targetIndex);
        displayedIndexRef.current = targetIndex;
        console.log(`[AnimatedText] Animation complete, displayed ${targetIndex} parts`);
      }
    });

    animationControlsRef.current = controls;
    prevTextRef.current = text;

    return () => {
      if (controls) {
        controls.stop();
      }
      animationControlsRef.current = null;
    };
  }, [text, delimiter]);

  // Return the displayed portion of text
  const parts = text.split(delimiter).filter(p => p.length > 0);
  return parts.slice(0, displayedIndex).join(delimiter);
}
