import React, { useState, useRef } from 'react';
import { HelpCircle, Menu, Settings } from 'lucide-react';
import SettingsModal from './SettingsModal';

// Header component - sidebar toggle moved to sidebar itself

interface HeaderProps {
  onToggleSidebar: () => void;
}

const Header: React.FC<HeaderProps> = ({ onToggleSidebar }) => {
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const settingsRef = useRef<HTMLButtonElement>(null);

  return (
    <>
      <header className="h-14 border-b border-gray-200 dark:border-zinc-700 bg-white dark:bg-zinc-900 sticky top-0 z-50 transition-colors duration-200">
        <div className="h-full max-w-full mx-auto px-4 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <button 
              className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-zinc-800 transition-colors lg:hidden" 
              onClick={onToggleSidebar}
              title="Toggle sidebar"
              aria-label="Toggle sidebar"
            >
              <Menu size={20} className="text-gray-700 dark:text-gray-300" />
            </button>
            <div className="flex items-center gap-2">
              <h1 className="text-xl font-semibold text-[color:var(--alcon-primary)]">
                EyeQ
              </h1>
              <span className="hidden sm:inline text-sm text-gray-500 dark:text-gray-400">
                MLR Compliance Assistant
              </span>
            </div>
          </div>
          <div className="flex items-center gap-1">
            <button 
              className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-zinc-800 transition-colors" 
              title="Help"
              aria-label="Help"
            >
              <HelpCircle size={18} className="text-gray-700 dark:text-gray-300" />
            </button>
            <button 
              ref={settingsRef}
              className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-zinc-800 transition-colors" 
              title="Settings" 
              onClick={() => setIsSettingsOpen(true)}
              aria-label="Settings"
            >
              <Settings size={18} className="text-gray-700 dark:text-gray-300" />
            </button>
          </div>
        </div>
      </header>
      <SettingsModal isOpen={isSettingsOpen} onClose={() => setIsSettingsOpen(false)} />
    </>
  );
};

export default Header;
