import React from 'react';

interface PromptSuggestionsProps {
  onSelectPrompt: (prompt: string) => void;
  isLoading: boolean;
}

const PROMPTS = [
  "Analyze this promotional text for FDA/FTC compliance",
  "Check if these claims are approved for Clareon PanOptix IOL",
  "Review this marketing material for regulatory issues",
  "Verify these claims against approved Total 30 Contact Lens claims",
  "Identify any missing disclaimers or safety information",
  "Check for unsubstantiated superlatives or overpromising language",
  "Extract and analyze text from this image for compliance",
  "Compare two versions of this document for compliance changes"
];

const PromptSuggestions: React.FC<PromptSuggestionsProps> = ({ onSelectPrompt, isLoading }) => {
  const handleClick = (prompt: string) => {
    if (!isLoading) {
      onSelectPrompt(prompt);
    }
  };

  return (
    <div className="w-full max-w-4xl mx-auto px-6 mt-6 mb-4">
      <div className="flex flex-wrap gap-2 justify-center">
        {PROMPTS.map((prompt, index) => (
          <button
            key={index}
            onClick={() => handleClick(prompt)}
            disabled={isLoading}
            className={`
              px-4 py-2.5 rounded-full text-sm font-medium
              transition-all duration-200 ease-in-out
              border border-chatgpt-border dark:border-chatgpt-dark-border
              bg-chatgpt-bg-secondary dark:bg-chatgpt-dark-bg-secondary
              text-chatgpt-text-primary dark:text-chatgpt-dark-text-primary
              hover:bg-chatgpt-bg-tertiary dark:hover:bg-chatgpt-dark-bg-tertiary
              hover:border-alcon-blue dark:hover:border-alcon-blue
              hover:shadow-chatgpt
              active:scale-95
              disabled:opacity-50 disabled:cursor-not-allowed disabled:hover:scale-100
              animate-slide-up
            `}
            style={{ animationDelay: `${index * 50}ms`, opacity: 0, animationFillMode: 'forwards' }}
            aria-label={`Use prompt: ${prompt}`}
          >
            {prompt}
          </button>
        ))}
      </div>
    </div>
  );
};

export default PromptSuggestions;
