import { useState, useRef, useEffect } from 'react';
import ChatInterface from './components/ChatInterface';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import { ThemeProvider } from './ThemeContext';
import { ChatProvider } from './ChatContext';

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const mainContentRef = useRef<HTMLDivElement>(null);

  // Set background image on main content area to align with ChatInterface
  useEffect(() => {
    if (mainContentRef.current) {
      mainContentRef.current.style.backgroundImage = "url('/assets/images/background.png')";
      mainContentRef.current.style.backgroundSize = 'cover';
      mainContentRef.current.style.backgroundPosition = 'center';
      mainContentRef.current.style.backgroundRepeat = 'no-repeat';
      mainContentRef.current.style.backgroundAttachment = 'fixed';
    }

    // Cleanup function to reset styles if component unmounts
    return () => {
      if (mainContentRef.current) {
        mainContentRef.current.style.backgroundImage = '';
        mainContentRef.current.style.backgroundSize = '';
        mainContentRef.current.style.backgroundPosition = '';
        mainContentRef.current.style.backgroundRepeat = '';
        mainContentRef.current.style.backgroundAttachment = '';
      }
    };
  }, []);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const closeSidebar = () => {
    setSidebarOpen(false);
  };

  const toggleSidebarCollapse = () => {
    setSidebarCollapsed(!sidebarCollapsed);
  };

  return (
    <ThemeProvider>
      <ChatProvider>
        {/* Main container - transparent to show background image */}
        <div className="flex h-screen bg-transparent">

          {/* Sidebar - will be static on desktop, fixed on mobile */}
          <Sidebar
            isOpen={sidebarOpen}
            isCollapsed={sidebarCollapsed}
            onClose={closeSidebar}
            onToggleCollapse={toggleSidebarCollapse}
          />
          
          {/* Main content area - relative positioning, takes remaining space */}
          <div ref={mainContentRef} className="flex-1 flex flex-col overflow-hidden relative z-10">
            <Header onToggleSidebar={toggleSidebar} />
            
            <div className="flex-1 overflow-hidden">
                           <ChatInterface 
                onToggleSidebar={toggleSidebar} 
                sidebarCollapsed={sidebarCollapsed}
                sidebarOpen={sidebarOpen}
              />
            </div>
          </div>
          
          {/* Alcon Logo Watermark */}
          <img
            src="/assets/images/alcon-logo.png"
            alt="Alcon"
            className="fixed bottom-6 right-6 h-6 opacity-15 dark:opacity-8 pointer-events-none z-20 transition-all duration-300 hover:opacity-25 dark:hover:opacity-15"
            onError={(e) => {
              const target = e.target as HTMLImageElement;
              target.src = "/assets/images/alcon-logo.png";
              target.onerror = () => {
                target.src = "/assets/images/alcon-logo.svg";
                target.onerror = () => {
                  target.style.display = 'none';
                };
              };
            }}
          />
        </div>
      </ChatProvider>
    </ThemeProvider>
  );
}

export default App;
