import React, { useEffect, useRef, useState } from 'react';

interface Particle {
  x: number;
  y: number;
  size: number;
  speedX: number;
  speedY: number;
  opacity: number;
  targetOpacity: number;
  twinkleSpeed: number;
  baseSize: number;
}

const ParticleBackground: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const animationFrameRef = useRef<number>();
  const particlesRef = useRef<Particle[]>([]);
  const [isIdle, setIsIdle] = useState(false);
  const idleTimerRef = useRef<NodeJS.Timeout>();
  const lastMouseMoveRef = useRef<number>(Date.now());

  // Particle configuration
  const PARTICLE_COUNT = 150;
  const IDLE_THRESHOLD = 3000; // 3 seconds
  const FADE_IN_SPEED = 0.02;
  const FADE_OUT_SPEED = 0.05;
  const MIN_OPACITY = 0.1;
  const MAX_OPACITY = 0.8;

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Set canvas size
    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };
    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    // Initialize particles - spawn from bottom right
    const initParticles = () => {
      particlesRef.current = [];
      for (let i = 0; i < PARTICLE_COUNT; i++) {
        // Spawn particles from bottom right corner area
        const spawnX = canvas.width * 0.7 + Math.random() * canvas.width * 0.3;
        const spawnY = canvas.height * 0.7 + Math.random() * canvas.height * 0.3;
        
        // Movement direction: generally upward and leftward (from bottom right)
        const angle = Math.random() * Math.PI * 0.75 + Math.PI * 0.5; // 90-247.5 degrees (up-left direction)
        const speed = Math.random() * 0.1 + 0.05; // Slower movement
        
        particlesRef.current.push({
          x: spawnX,
          y: spawnY,
          size: Math.random() * 2 + 1,
          baseSize: Math.random() * 2 + 1,
          speedX: Math.cos(angle) * speed, // Negative (leftward)
          speedY: Math.sin(angle) * speed, // Negative (upward)
          opacity: 0,
          targetOpacity: Math.random() * (MAX_OPACITY - MIN_OPACITY) + MIN_OPACITY,
          twinkleSpeed: Math.random() * 0.02 + 0.01,
        });
      }
    };

    initParticles();

    // Idle detection
    const handleMouseMove = () => {
      lastMouseMoveRef.current = Date.now();
      clearTimeout(idleTimerRef.current);
      setIsIdle(false);

      idleTimerRef.current = setTimeout(() => {
        setIsIdle(true);
      }, IDLE_THRESHOLD);
    };

    // Initial idle check
    idleTimerRef.current = setTimeout(() => {
      setIsIdle(true);
    }, IDLE_THRESHOLD);

    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('scroll', handleMouseMove);
    window.addEventListener('touchstart', handleMouseMove);

    // Animation loop
    const animate = () => {
      if (!ctx) return;

      // Clear canvas completely each frame
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Update and draw particles
      particlesRef.current.forEach((particle, index) => {
        // Update position - slower movement
        particle.x += particle.speedX;
        particle.y += particle.speedY;

        // If particle goes off screen, respawn from bottom right
        if (particle.x < 0 || particle.x > canvas.width || particle.y < 0 || particle.y > canvas.height) {
          // Respawn from bottom right
          particle.x = canvas.width * 0.7 + Math.random() * canvas.width * 0.3;
          particle.y = canvas.height * 0.7 + Math.random() * canvas.height * 0.3;
          particle.opacity = 0; // Start invisible
          
          // Reset movement direction
          const angle = Math.random() * Math.PI * 0.75 + Math.PI * 0.5;
          const speed = Math.random() * 0.1 + 0.05;
          particle.speedX = Math.cos(angle) * speed;
          particle.speedY = Math.sin(angle) * speed;
        }

        // Twinkling effect (size variation)
        const twinkle = Math.sin(Date.now() * particle.twinkleSpeed) * 0.3 + 1;
        particle.size = particle.baseSize * twinkle;

        // Update opacity based on idle state
        if (isIdle) {
          // Fade in when idle
          if (particle.opacity < particle.targetOpacity) {
            particle.opacity = Math.min(
              particle.opacity + FADE_IN_SPEED,
              particle.targetOpacity
            );
          }
        } else {
          // Fade out when active
          particle.opacity = Math.max(particle.opacity - FADE_OUT_SPEED, 0);
        }

        // Draw particle
        if (particle.opacity > 0) {
          ctx.beginPath();
          ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
          
          // Create glow effect
          const gradient = ctx.createRadialGradient(
            particle.x,
            particle.y,
            0,
            particle.x,
            particle.y,
            particle.size * 2
          );
          gradient.addColorStop(0, `rgba(255, 255, 255, ${particle.opacity})`);
          gradient.addColorStop(0.5, `rgba(255, 255, 255, ${particle.opacity * 0.5})`);
          gradient.addColorStop(1, `rgba(255, 255, 255, 0)`);
          
          ctx.fillStyle = gradient;
          ctx.fill();

          // Add bright center point
          ctx.beginPath();
          ctx.arc(particle.x, particle.y, particle.size * 0.3, 0, Math.PI * 2);
          ctx.fillStyle = `rgba(255, 255, 255, ${particle.opacity})`;
          ctx.fill();
        }
      });

      animationFrameRef.current = requestAnimationFrame(animate);
    };

    animate();

    // Cleanup
    return () => {
      window.removeEventListener('resize', resizeCanvas);
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('scroll', handleMouseMove);
      window.removeEventListener('touchstart', handleMouseMove);
      clearTimeout(idleTimerRef.current);
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [isIdle]);

  return (
    <canvas
      ref={canvasRef}
      className="absolute inset-0 pointer-events-none z-0"
      style={{ background: 'transparent' }}
    />
  );
};

export default ParticleBackground;
