/**
 * API Configuration
 * 
 * Centralized API endpoint configuration that supports both development and production.
 * In development, uses the proxy from package.json (localhost:5000)
 * In production, uses REACT_APP_API_URL environment variable
 */

// Get API base URL from environment variable, fallback to empty string for proxy in dev
const API_BASE_URL = process.env.REACT_APP_API_URL || '';

/**
 * API Endpoints
 * 
 * All API endpoints used throughout the application.
 * Uses relative paths in development (via proxy) or absolute URLs in production.
 */
export const API_ENDPOINTS = {
  analyze: `${API_BASE_URL}/api/analyze`,
  upload: `${API_BASE_URL}/api/upload`,
  share: `${API_BASE_URL}/api/share`,
  export: (id: string) => `${API_BASE_URL}/api/export/${id}`,
  feedback: `${API_BASE_URL}/api/feedback`,
  health: `${API_BASE_URL}/api/health`,
  compare: `${API_BASE_URL}/api/compare`,
  knowledge: (query: string) => `${API_BASE_URL}/api/knowledge?q=${encodeURIComponent(query)}`,
  shareGet: (shareId: string) => `${API_BASE_URL}/api/share/${shareId}`,
  shareComment: (shareId: string) => `${API_BASE_URL}/api/share/${shareId}/comment`,
  collaborationStats: (conversationId: string) => `${API_BASE_URL}/api/collaboration/stats/${conversationId}`,
};

/**
 * Get the full API URL for a given endpoint
 * Useful for debugging or logging
 */
export const getApiUrl = (endpoint: string): string => {
  return endpoint.startsWith('http') ? endpoint : `${API_BASE_URL}${endpoint}`;
};

/**
 * Check if running in production
 */
export const isProduction = (): boolean => {
  return process.env.NODE_ENV === 'production';
};
