"use client";

import { animate } from "framer-motion";
import { useEffect, useState, useRef } from "react";

export function useAnimatedText(text: string, delimiter: string = "") {
  const [cursor, setCursor] = useState(0);
  const prevTextRef = useRef(text);
  const cursorRef = useRef(0);
  const animationControlsRef = useRef<ReturnType<typeof animate> | null>(null);

  // Keep cursor ref in sync
  useEffect(() => {
    cursorRef.current = cursor;
  }, [cursor]);

  useEffect(() => {
    // Handle empty text
    if (!text) {
      setCursor(0);
      cursorRef.current = 0;
      prevTextRef.current = '';
      if (animationControlsRef.current) {
        animationControlsRef.current.stop();
        animationControlsRef.current = null;
      }
      return;
    }

    const parts = text.split(delimiter);
    const prevText = prevTextRef.current;
    const currentCursor = cursorRef.current;
    
    // Determine starting point: if text continues from previous, use current cursor, otherwise reset
    let startIndex = 0;
    if (text.startsWith(prevText) && prevText.length > 0) {
      // Text is incremental - continue from where we are
      startIndex = currentCursor;
    } else {
      // Text is new or changed - start from beginning
      startIndex = 0;
      setCursor(0);
      cursorRef.current = 0;
    }

    const targetIndex = parts.length;
    const remainingParts = targetIndex - startIndex;
    
    // If no remaining parts, we're done
    if (remainingParts <= 0) {
      prevTextRef.current = text;
      return;
    }

    // Stop any existing animation
    if (animationControlsRef.current) {
      animationControlsRef.current.stop();
    }

    // Calculate duration per part (in seconds)
    const durationPerPart = delimiter === "" ? 0.02 : // Character: 20ms per character
                            delimiter === " " ? 0.12 : // Word: 120ms per word
                            0.5; // Chunk: 500ms per chunk
    
    // Duration for remaining parts
    const duration = Math.max(remainingParts * durationPerPart, 0.2);
    
    const controls = animate(startIndex, targetIndex, {
      duration: duration,
      ease: "easeOut",
      onUpdate(latest) {
        const newCursor = Math.floor(latest);
        setCursor(newCursor);
        cursorRef.current = newCursor;
      },
      onComplete: () => {
        // Ensure we're at the target when animation completes
        setCursor(targetIndex);
        cursorRef.current = targetIndex;
      }
    });

    animationControlsRef.current = controls;
    prevTextRef.current = text;

    return () => {
      if (controls) {
        controls.stop();
      }
      animationControlsRef.current = null;
    };
  }, [text, delimiter]);

  return text.split(delimiter).slice(0, cursor).join(delimiter);
}
