"use client";

import { animate } from "framer-motion";
import { useEffect, useState, useRef } from "react";

export function useAnimatedText(text: string, delimiter: string = "") {
  const [cursor, setCursor] = useState(0);
  const [startingCursor, setStartingCursor] = useState(0);
  const prevTextRef = useRef(text);
  const cursorRef = useRef(0);

  // Keep cursor ref in sync
  useEffect(() => {
    cursorRef.current = cursor;
  }, [cursor]);

  // Update starting cursor when text changes incrementally
  if (prevTextRef.current !== text) {
    const prevText = prevTextRef.current;
    // If new text starts with previous text, continue from current cursor
    // Otherwise, reset to beginning
    const newStartingCursor = text.startsWith(prevText) ? cursorRef.current : 0;
    setStartingCursor(newStartingCursor);
    prevTextRef.current = text;
  }

  useEffect(() => {
    const parts = text.split(delimiter);
    const remainingParts = parts.length - startingCursor;
    
    if (remainingParts <= 0) {
      setCursor(parts.length);
      return;
    }

    // Calculate duration per part (in seconds) - slower for smoother animation
    const durationPerPart = delimiter === "" ? 0.01 : // Character: 10ms per character
                            delimiter === " " ? 0.08 : // Word: 80ms per word (smooth reading speed)
                            0.3; // Chunk: 300ms per chunk
    
    // Total duration based on remaining parts
    // Cap at reasonable maximum to avoid very long animations
    const totalDuration = Math.min(remainingParts * durationPerPart, 10);
    
    const controls = animate(startingCursor, parts.length, {
      duration: totalDuration,
      ease: "easeOut",
      onUpdate(latest) {
        setCursor(Math.floor(latest));
      },
    });

    return () => controls.stop();
  }, [startingCursor, text, delimiter]);

  return text.split(delimiter).slice(0, cursor).join(delimiter);
}
